INSERT INTO `sys_oper_log` VALUES (274, '电影信息', 1, 'com.ruoyi.web.controller.film.FilmController.add()', 'POST', 1, 'admin', NULL, '/film/film', '127.0.0.1', '内网IP', '{\"describe\":\"因一场电梯意外，钢铁直男胡铁男（常远 饰）意外进入一个“错位”世界，昔日成功学大师胡铁男变身“全职家庭主夫”，老酒友聚会变成下午茶比美大会，层出不穷的反差让胡铁男着实无法忍受。。 [8] \\n面对颠倒错乱的世界，胡铁男在“鸡飞狗跳”的生活中体验了真正的“换位”，终于从对妻子的忽视逐渐变成感同身受，终于明白了妻子的默默付出与不易，明白了“爱”的真义……\",\"director\":\"张琦\",\"duration\":120,\"eName\":\"Desperate Master\",\"language\":\"中文\",\"name\":\"绝望主夫\",\"pImg\":\"/profile/upload/2022/12/27/jwzf_20221227103445A003.png\",\"pNumber\":2133424,\"params\":{},\"poster\":\"/profile/upload/2022/12/27/jwzf_20221227103443A002.png\",\"price\":\"99.9\",\"releaseDate\":\"2022-12-31\",\"released\":0,\"score\":9.0,\"src\":\"大陆\",\"star\":\"常远\",\"status\":1,\"type\":\"喜剧\",\"vImg\":\"/profile/upload/2022/12/27/jwzf_20221227103434A001.png\",\"version\":\"2D\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2022-12-27 10:36:09');
INSERT INTO `sys_oper_log` VALUES (275, '电影信息', 1, 'com.ruoyi.web.controller.film.FilmController.add()', 'POST', 1, 'admin', NULL, '/film/film', '127.0.0.1', '内网IP', '{\"describe\":\"在抗美援朝战争鲜为人知的“冷枪冷炮”运动中，由班长刘文武（章宇饰）带领的“冷枪五班”战士为营救侦察兵亮亮（刘奕铁饰），在残酷的战场上以最壮烈的一次集结，与敌军展开以枪换命的热血捍卫。在以美国为主力的联合国军猛烈的炮火攻击下，志愿军无数条年轻的生命倒在了战场上，再也听不见战友的声声呼唤\",\"director\":\"张艺谋、张末\",\"duration\":96,\"eName\":\"Sniper\",\"language\":\"中文\",\"name\":\"狙击手\",\"pImg\":\"/profile/upload/2022/12/27/jjshaibao_20221227103931A006.png\",\"pNumber\":21314,\"params\":{},\"poster\":\"/profile/upload/2022/12/27/jjshaibao_20221227103928A005.png\",\"price\":\"43\",\"releaseDate\":\"2022-02-01\",\"released\":1,\"score\":9.5,\"src\":\"大陆\",\"star\":\"陈永胜、章宇、张译、林博洋、刘奕铁、黄炎、王梓屹、陈铭杨、王乃训、程泓鑫\",\"status\":3,\"type\":\"动作\",\"vImg\":\"/profile/upload/2022/12/27/jjs_20221227103925A004.png\",\"version\":\"2D\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2022-12-27 10:40:58');
INSERT INTO `sys_oper_log` VALUES (276, '电影信息', 1, 'com.ruoyi.web.controller.film.FilmController.add()', 'POST', 1, 'admin', NULL, '/film/film', '127.0.0.1', '内网IP', '{\"describe\":\"努米亚共和国爆发战乱，由于使馆人手不够，原本只是协助撤侨工作，当天往返回国的前驻地外交官宗大伟（张译饰）与外交部新人成朗（王俊凯饰）临危受命，主动放弃回家机会，支援撤侨行动。任务顺利结束，却得知还有一批被困同胞，正在白婳（殷桃饰）的带领下，前往边境撤离点。情急之下，两人放弃了回家机会，逆行进入战区。赤手空拳的外交官，穿越战火和荒漠，面对反叛军的枪口，他们要带领同胞走出一条回家之路\",\"director\":\"饶晓志\",\"duration\":137,\"eName\":\"Home Coming\",\"language\":\"中文\",\"name\":\"万里归途\",\"pImg\":\"/profile/upload/2022/12/27/wlgthaibao_20221227104625A003.png\",\"pNumber\":214343,\"params\":{},\"poster\":\"/profile/upload/2022/12/27/wlgthaibao_20221227104622A002.png\",\"price\":\"43\",\"releaseDate\":\"2022-09-30\",\"released\":1,\"score\":9.5,\"src\":\"大陆\",\"star\":\"张译 、王俊凯、殷桃、成泰燊、张子贤、陈昊宇、王迅、万茜、李雪健、李晨\",\"status\":3,\"type\":\"战争\",\"vImg\":\"/profile/upload/2022/12/27/wlgt_20221227104619A001.png\",\"version\":\"2D\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2022-12-27 10:47:27');
INSERT INTO `sys_oper_log` VALUES (277, '电影信息', 1, 'com.ruoyi.web.controller.film.FilmController.add()', 'POST', 1, 'admin', NULL, '/film/film', '127.0.0.1', '内网IP', '{\"describe\":\"年轻女子基娅·克拉克被家人抛弃，在北卡罗来纳州中一片危险的沼泽地里独自生活并长大。多年来，巴克利湾一直萦绕着一则关于“沼泽女孩”的传言，导致聪明坚强的基娅遭到当地居民的排斥。有一天，基娅被镇上来的两位年轻人所吸引，面对一个全新的、令人震撼的世界，她敞开了自己的心扉。但是，在其中一位年轻人的尸体被发现后，她立刻被小镇认定为头号嫌疑人。随着案件调查取得新的进展，真相反而变得愈发难以捉摸。沼泽内暗藏的诸多秘密也渐渐地被揭开\",\"director\":\"奥利维亚·纽曼\",\"duration\":125,\"eName\":\"Where the Crawdads Sing\",\"language\":\"英语\",\"name\":\"沼泽深处的女孩\",\"pImg\":\"/profile/upload/2022/12/27/zzscdnh_20221227105029A006.png\",\"pNumber\":34243,\"params\":{},\"poster\":\"/profile/upload/2022/12/27/zzscdnh_20221227105027A005.png\",\"price\":\"36\",\"releaseDate\":\"2022-11-25\",\"released\":1,\"score\":9.3,\"src\":\"欧美\",\"star\":\"黛西·埃德加-琼斯、哈里斯·迪金森\",\"status\":3,\"type\":\"悬疑\",\"vImg\":\"/profile/upload/2022/12/27/zzscdnh_20221227105023A004.png\",\"version\":\"2D\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2022-12-27 10:51:54');
INSERT INTO `sys_oper_log` VALUES (278, '电影信息', 1, 'com.ruoyi.web.controller.film.FilmController.add()', 'POST', 1, 'admin', NULL, '/film/film', '127.0.0.1', '内网IP', '{\"describe\":\"熊强卷入了“铁甲怪”抢劫团的绑架事件，意外发现有个和妈妈长得很像的白熊。因当年被妈妈抛弃而无法释怀的熊大，冒险途中认出白熊就是妈妈。随着反派浮出水面，熊大得知了当年的真相，最终敞开心扉，相信妈妈的爱\",\"director\":\"徐芸、崔铁志、刘圳杰\",\"duration\":95,\"eName\":\"Boonie Bears:Guardian code\",\"language\":\"中文\",\"name\":\"熊出没·伴我“熊芯”\",\"pImg\":\"/profile/upload/2022/12/27/xcmhaibao_20221227105512A009.png\",\"pNumber\":3214342,\"params\":{},\"poster\":\"/profile/upload/2022/12/27/xcmhaibao_20221227105509A008.png\",\"price\":\"99.9\",\"releaseDate\":\"2023-01-22\",\"released\":0,\"score\":10.0,\"src\":\"大陆\",\"star\":\"张秉君、谭笑、张伟、贾晨露、王天昊、王思雨、缪莹莹、聂吉轩、张茗\",\"status\":1,\"type\":\"动画\",\"vImg\":\"/profile/upload/2022/12/27/xcm_20221227105506A007.png\",\"version\":\"2D\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2022-12-27 10:56:40');
